import React from 'react';
import { useLoaderData } from 'react-router-dom';
import Post from '../Post/Post';

const Posts = () => {
    const posts = useLoaderData();
    console.log(posts);
    return (
        <div>
            <h2>A lot of posts are here: {posts.length}</h2>

            {
                posts.map(post => <Post
                    key={post.id}
                    post={post}
                    ></Post>
                    )
            }
        
        
        </div>
    );
};

export default Posts;
